import React from 'react';
import { useRef, useState } from "react";
import PianoRoll from "react-piano-roll";

export class PianoRollManagement extends React.Component {
    render() {
        function variables() {
            const [state, setState] = useState(0);
            const playbackRef = useRef();
            window.addEventListener("keydown", ({key}) => {
                if (playbackRef.current === undefined) {
                    return;
                } else if (key === " ") {
                    playbackRef.current.toggle();
                } else if (key === "Enter") {
                    playbackRef.current.seek("0:0:0");
                }
            });
        }
        return (
            <div className="App">
                <PianoRoll
                    width={810}
                    height={400}
                    zoom={6}
                    // resolution={2}
                    gridLineColor={0x333333}
                    blackGridBgColor={0x1e1e1e}
                    whiteGridBgColor={0x282828}
                    noteData={[
                        ["0:0:0", "C4", "2n"],
                        ["0:0:0", "D4", "2n"],
                        ["0:0:0", "E4", "2n"],
                        ["0:2:0", "B4", "4n"],
                        ["0:3:0", "A#4", "4n"]
                    ]}
                    ref={playbackRef}
                />
                <button onClick={() => setState(state + 1)}>set state</button>
            </div>
        );
    }
}
